document.getElementById('toolSearch')&&document.getElementById('toolSearch').addEventListener('input',e=>{const q=e.target.value.toLowerCase();document.querySelectorAll('.tool-card').forEach(c=>{c.style.display=c.textContent.toLowerCase().includes(q)?'block':'none'})});const d=document.getElementById('darkCss'),t=document.getElementById('themeToggle');if(t&&d){const a=()=>{localStorage.getItem('theme')==='dark'&&(d.media='all')};t.addEventListener('click',()=>{d.media=d.media==='all'?'(prefers-color-scheme: dark)':'all';d.media==='all'?localStorage.setItem('theme','dark'):localStorage.removeItem('theme');});a();}
document.querySelectorAll('.copy-btn').forEach(b=>{const t=document.getElementById(b.dataset.target)||document.querySelector(`[name='${b.dataset.target}'],#${b.dataset.target}`);b.addEventListener('click',()=>{if(t){const val = t.value!==undefined ? t.value : t.textContent;navigator.clipboard.writeText(val);b.textContent='Copied!';setTimeout(()=>b.textContent='Copy',1000);}});});
